  map.addListener('click', e =>{ //ピンどめの処理
    if (!isEnabled.checked) {
      return;
    }
    if (marker) {
      marker.setMap(null);
      lat.value = null;
      lng.value = null;
    }
    geocoder.geocode(
      {location: e.latLng},
      (results, status) => {
        if (status !== 'OK') {
          alert('Faild: ' + status + ':国内の陸地を選択してください');
          return;
        }
        console.log(results);
        if (getCountryName(results) != '日本') {
          alert('国内の陸地を選択してください');
          return;
        }
        if (getPrefectureName(results) == null) {
          alert('国内の陸地を選択してください');
          return;
        }
        if (results[0]) {
          marker = new google.maps.Marker({
            position: e.latLng,
            map: map,
            title: '発見場所',
            animation: google.maps.Animation.DROP,
          });
          lat.value = e.latLng.lat().toFixed(6);
          lng.value = e.latLng.lng().toFixed(6);
        }
        selectPrefecture.options.selectedIndex = prefectures.indexOf(getPrefectureName(results));
        substitute.textContent = getPrefectureName(results);

        console.log(getCountryName(results));
        console.log(getPrefectureName(results));
      })
  });

  selectPrefecture.addEventListener('click',()=>{
    lat.readOnly = true;
    lng.readOnly = true;
    lat.value = null;
    lng.value = null;
    map.setOptions({
      // center: tokyo,
      zoom: 15,
      disableDefaultUI: true,
      scrollwheel: false,
      gestureHandling: 'none',
      clickableIcons: false,
    });
    if (marker) {
      marker.setMap(null);
    }
  });

});
