onPageLoad('articles#new', ()=>{

  console.log('rendering_map...');
  var target = document.getElementById('mapTarget');
  var map;
  var marker;

  var tokyo = {lat: 35.681167, lng: 139.767052};

  var lat = document.getElementById('map_lat');
  var lng = document.getElementById('map_lng');

  var isEnabled = document.getElementById('map_is_enabled');



  if (isEnabled.checked) {
    console.log('on');
  } else {
    console.log('off');
  }

  isEnabled.addEventListener('change',()=>{//mapの有効無効を変更したとき
    if (isEnabled.checked) {
      lat.readOnly = false;
      lng.readOnly = false;
      map.setOptions({
        center: tokyo,
        zoom: 15,
        disableDefaultUI: false,
        scrollwheel: true,
      });
    } else {
      lat.readOnly = true;
      lng.readOnly = true;
      lat.value = null;
      lng.value = null;
      map.setOptions({
        center: tokyo,
        zoom: 15,
        disableDefaultUI: true,
        scrollwheel: false,
      });
      if (marker) {
        marker.setMap(null);
      }
    }
  });


  // var location = {
  //   lat: parseFloat(taskLat.value),
  //   lng: parseFloat(taskLng.value),
  // }
  // var taskLat = ;
  // var taskLngt = ;

  var geocorder = '';

  map = new google.maps.Map(target, {
    center: tokyo,
    zoom: 15
  });

  map.addListener('click', e =>{
    if (marker) {
      marker.setMap(null);
    }
    marker = new google.maps.Marker({
      position: e.latLng,
      map: map,
      title: '発見場所',
      animation: google.maps.Animation.DROP,
    });
    lat.value = e.latLng.lat().toFixed(6);
    lng.value = e.latLng.lng().toFixed(6);
  });
});
